import streamlit as stfrom utilites.data_loading import (match_results,img,                          home_team,away_team,df, home_goals, away_goals,                          home_non_goals,away_non_goals)from visualizations.goals_viz import show_goals_vizfrom visualizations.lineup_viz import show_lineup_vizfrom visualizations.heat_maps import hm_mainfrom visualizations.pass_network_viz import pn_mainfrom visualizations.compare import xg_viz, xg_shots        def show_header(df, match_home, match_away, choosed_match):     #Shows team logos, scores and goals    #Team logos are in "team logos" file        home_img = img + match_home.lower() + '.png'    away_img = img + match_away.lower() + '.png'    #Creates columns for show match info    #Home team logo + Score + Away team logo    col1,col2,col3,col4,col5 = st.beta_columns([2,3,1,2,2])    col1.image(home_img); col2.title(match_results['home_team'][choosed_match]); col3.title(match_results['Score'][choosed_match])    col4.title(match_results['away_team'][choosed_match]); col5.image(away_img);        #Shows goals    col2.text(home_goals[['team','player','minute']].to_string(index = False, header=False))    col4.text(away_goals[['team','player','minute']].to_string(index = False, header=False))       def show_viz_menu(df, match_home, match_away):    col1,col2,col3,col4,col5 = st.beta_columns(5)    choosed_compare = col5.selectbox('',['Compare Type','Pass Networks', 'Shots Map by xG']) #Different selectbox for compare the teams    if(col1.button("Line Up")):        st.title("Lineups")        st.pyplot(show_lineup_viz()) #Calls lineup_viz.py    if(col2.button("Goals")):        st.pyplot(show_goals_viz(match_home, match_away)) #Calls goals_viz.py    if(col3.button("Heat Maps")):        st.title(f"{home_team}'s and {away_team}'s pass and heat maps")        st.pyplot(hm_main()) #Calls heat_maps.py    if(col4.button("xG Expected Goals")):        st.pyplot(xg_viz()) #Calls xg_viz() from compare.py    if(choosed_compare == 'Pass Networks'):        col1,col2 = st.beta_columns(2)        col1.title(f"{home_team}'s pass networks")        col2.title(f"{away_team}'s pass networks")        col1.pyplot(pn_main(home_team)) #Call pass_network_viz.py        col2.pyplot(pn_main(away_team)) ##Call pass_network_viz.py    if(choosed_compare == 'Shots Map by xG'):        col1, col2 = st.beta_columns(2)        col1.title(f'{home_team} Shots by Using xG')        col2.title(f'{away_team} Shots by Using xG')        col1.pyplot(xg_shots(home_non_goals, home_goals)) #Calls xg_shots() from compare.py        col2.pyplot(xg_shots(away_non_goals, away_goals)) #Calls xg_shots() from compare.py        def show_match_viz(match_round, home_team, away_team, choosed_match):    #Show head and visualization menu    show_header(df, home_team, away_team, choosed_match)    show_viz_menu(df, home_team, away_team)    